/**
 * Define an error
 * @function apemanerror
 * @param {string} msg - Error message
 * @param {Object} properties - Error properties
 * @returns {Error}
 */
'use strict'

/** @lends apemanerror */
function apemanerror (msg, properties) {
  return Object.assign(new Error(msg), properties)
}

module.exports = apemanerror
