/**
 * Test case for digest.
 * Runs with mocha.
 */
'use strict'

const digest = require('../lib/digest.js')
const assert = require('assert')
const co = require('co')

describe('digest', function () {
  this.timeout(3000)

  before(() => co(function * () {

  }))

  after(() => co(function * () {

  }))

  it('Digest', () => co(function * () {
    let d = digest()
    let salt = d.newSalt()
    assert.equal(yield d('hoge', salt), yield d('hoge', salt))
  }))
})

/* global describe, before, after, it */
