/**
 * Print env settings.
 * @function print
 * @param {object} schemas - schemas to print.
 */

'use strict'

const colorprint = require('colorprint')
const { colors } = colorprint
const yaml = require('js-yaml')

/** @lends print */
function print (schemas) {
  let values = Object.assign({}, schemas.toJSON())
  colorprint.debug('')
  colorprint.debug('Schemas')
  colorprint.debug('------------')
  let msg = yaml.safeDump(values)
  colorprint.debug(colors.blackBright(msg))
  colorprint.debug('')
  colorprint.debug('')
}

module.exports = print;
